INSERT INTO `person_table` VALUES (19, 'Javaæ–‡ä»¶çš„å†™å…¥äºè¯»å–', 'FileReaderå’ŒBufferedReaderçš„åŒºåˆ«', 'https://qiniu.lllcnm.cn/img/a488aa7c-d318-492d-b4d3-2ca3b19d1a4e.jpg', 'Java', '2023-06-28', '2023-09-11', 0, '> FileReaderæ˜¯åŸºæœ¬çš„å­—ç¬¦è¾“å…¥æµï¼Œç›´æ¥ä»æ–‡ä»¶ä¸­è¯»å–å­—ç¬¦ã€‚\n  BufferedReaderæ˜¯ä½¿ç”¨ç¼“å†²åŒºçš„å­—ç¬¦è¾“å…¥æµï¼Œæä¾›äº†æ›´é«˜æ•ˆçš„è¯»å–æ–¹å¼ï¼Œå¯ä»¥ä¸€æ¬¡è¯»å–å¤šä¸ªå­—ç¬¦ï¼Œå¹¶ä¸”å¯ä»¥æŒ‰è¡Œè¯»å–æ–‡æœ¬ã€‚\n\n- å®šä¹‰ä¸€ä¸ªæ–‡ä»¶è·¯å¾„\n``` java\nprivate static final String filepath = \"./test.txt\";\n```\nåœ¨å½“å‰ç›®å½•ä¸‹å®šä¹‰ä¸€ä¸ªåä¸ºtestçš„æ–‡ä»¶\n## ä¸‹é¢æ˜¯ä¸€ä¸ªFileçš„æ–‡ä»¶çš„è¯»å–äºå†™å…¥\n``` java\n  @Test\n    //æ–‡ä»¶çš„å†™å…¥\n    public void IOWrite() throws IOException {\n        File file = new File(filepath);\n        //å¦‚æœæ–‡ä»¶ä¸å­˜åœ¨æ–°å»ºæ–‡ä»¶\n        if(!file.exists())\n            file.createNewFile();\n        FileWriter writer = new FileWriter(file);\n        writer.write(\"this is test file\");\n        writer.flush();\n        writer.close();//å…³é—­æ–‡ä»¶\n    }\n    //æ–‡ä»¶è¯»å–\n    @Test\n    public void IORead() throws IOException {\n        File file = new File(filepath);\n        FileReader reader = null;\n        try {\n            reader = new FileReader(file);\n            char[] ch = new char[(int) file.length()];\n            int bytesRead = reader.read(ch);\n            String fileContent = new String(ch, 0, bytesRead);\n            System.out.println(fileContent);\n        } finally {\n            if (reader == null) {\n                System.out.println(\"Reader is null\");\n            }\n            reader.close();\n        }\n    }\n```\nä¸Šé¢æˆ‘ä»¬ä½¿ç”¨çš„æ˜¯åŸºç¡€çš„FileWriteå†™å…¥å’ŒFielReaderè¯»å–\n## BufferedWriteçš„å†™å…¥ä¸BufferedReaderçš„è¯»å–\n``` java\n @Test\n    public void IOBufferedWrite() throws IOException {\n        File file = new File(filepath);\n        //å¦‚æœæ–‡ä»¶ä¸å­˜åœ¨ï¼Œåˆ›å»ºæ–‡ä»¶\n        if (!file.exists())\n            file.createNewFile();\n        //åˆ›å»ºBufferedReaderå¯¹è±¡å¹¶å‘æ–‡ä»¶å†™å…¥å†…å®¹\n        BufferedWriter bufferedWriter = new BufferedWriter(new FileWriter(file,true)); // ä½¿ç”¨trueè¡¨ç¤ºåœ¨åŸæœ‰å†…å®¹åŸºç¡€ä¸Šå¢åŠ å†…å®¹\n        bufferedWriter.write(\"\\nThis is Buffered write test file\");\n        bufferedWriter.flush();\n        bufferedWriter.close();\n        System.out.println(\"å†™å…¥æˆåŠŸ\");\n    }\n    @Test\n    public void IOBufferedReader() throws IOException {\n        File file = new File(filepath);\n        BufferedReader bufferedReader = new BufferedReader(new FileReader(file));\n        String line;\n        while ((line=bufferedReader.readLine()) != null) {\n            System.out.println(line);\n        }\n        bufferedReader.close();\n    }\n```\nä¸Šé¢å°±æ˜¯Bufferçš„è¯»å–ä¸å†™å…¥äº†', 1, 1);
